import React, { useState } from "react";
import "./Dashboard.css";

export default function Dashboard() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const closeSidebar = () => {
    setSidebarOpen(false);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-container">
        {/* Sidebar */}
        <div className={`sidebar ${sidebarOpen ? "active" : ""}`}>
          <div className="sidebar-header">
            <div className="logo">
              <span className="logo-icon">ðŸ“Š</span>
              <span className="logo-text">My Dashboard</span>
            </div>
          </div>
          <div className="sidebar-nav">
            <a href="/" className="nav-item active">Overview</a>
            <a href="/" className="nav-item">Projects</a>
            <a href="/" className="nav-item">Tasks</a>
            <a href="/" className="nav-item">Reports</a>
          </div>
          <div className="sidebar-footer">
            <a href="/" className="nav-item">Settings</a>
          </div>
        </div>

        {/* Overlay for mobile */}
        <div
          className={`sidebar-overlay ${sidebarOpen ? "active" : ""}`}
          onClick={closeSidebar}
        ></div>

        {/* Main content */}
        <div className={`main-content ${sidebarOpen ? "shifted" : ""}`}>
          <div className="content-header">
            {/* Hamburger button */}
            <button className="menu-toggle" onClick={toggleSidebar}>
              â˜°
            </button>
            <h1 className="page-title">Dashboard</h1>
          </div>

          <div className="issues-container">
            <div className="issue-section">
              <h2 className="section-header">Open Issues</h2>
              <div className="issue-list">
                <div className="issue-item">
                  <div className="issue-id">#101</div>
                  <div className="issue-title">Fix mobile sidebar</div>
                  <div className="issue-status green">âœ“</div>
                </div>
                <div className="issue-item">
                  <div className="issue-id">#102</div>
                  <div className="issue-title">Add dark mode</div>
                  <div className="issue-status yellow">â€¢</div>
                </div>
              </div>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}
